mapper.select:
     SELECT COUNT(*)  FROM  T_ACCOUNT WHERE  ACC_CA_NUMBER = #{cardNum}

			AND ACC_CREATE_TIME >= TO_DATE(CONCAT(#{beginTime},' 00:00:00'),'yyyy-mm-dd hh24:mi:ss')


			AND ACC_CREATE_TIME <= TO_DATE(CONCAT(#{overTime},' 23:59:59'),'yyyy-mm-dd hh24:mi:ss')


			AND ACC_TYPE  = #{type}
